import React from 'react';
import Tasks from '../components/Tasks.js'

export default function NavBar(props){
    if (window.location.hash === "complete"){
        return (
        <div class = "card text">
            <div class="card-header">
                <ul class="nav nav-tabs card-header-tabs">
                    <li class="nav-item">
                        <a class="nav-link active" href="#complete">complete tasks</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" href="#incomplete">incomplete tasks</a>
                    </li>
                </ul>
            </div>

            <div class="card-body">
                    <Tasks
                        //list={props.allTasks.filter((task)=> task.completeStatus === true)}
                        onRemoveTask={props.removeMethod} 
                        onToggleTask={props.toggleMethod}
                    />
            </div>
        </div>
        )}

    return (
        <div class = "card text">
            <div class="card-header">
                <ul class="nav nav-tabs card-header-tabs">
                    <li class="nav-item">
                        <a class="nav-link" href="#complete">complete tasks</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" href="#incomplete">incomplete tasks</a>
                    </li>
                </ul>
            </div>

            <div class="card-body">
                    <Tasks
                        //list={this.state.allTasks.filter((task)=> task.completeStatus === false)}
                        onRemoveTask={this.handleRemoveTask} 
                        onToggleTask={this.handleToggleCompletion}
                    />
            </div>
        </div>
    )


}
