import React from 'react';

export default function Input (props){

    return (
        <div>
            <input
                type='text'
                placeholder = 'new task'
                value={props.value}
                onChange={props.onInputChange}
            />
            <button onClick = {props.onAddTask()}>submit</button>

            <div>
                <button onClick={props.onClearTasks()}>clear all
                </button>
            </div>
        </div>

    )
}
