import React from 'react';

export default function Tasks (props) {
    if(props.windowHash === '#incomplete') {
        return (
            <div>
                <ol>
                    {props.list.map((task)=>(
                        <li key={task.description}>
                            <span>{task.description}</span>
                            <button id="btns" type="button" class="btn btn-secondary btn-sm" onClick={()=> props.onRemoveTask(task.description)}>remove</button>
                            <button id="btns" type="button" class={filterButton(task)} onClick={()=> props.onToggleTask(task.description)}>{filterTask(task)}</button>
                        </li>
                    ).filter((task) => task.completeStatus === false))}
                </ol>
            </div>

        )
    }
    if (props.windowHash === '#complete'){
        return (
            <div>
            <ol>
                {props.list.map((task)=>(
                    <li key={task.description}>
                        <span>{task.description}</span>
                        <button id="btns" type="button" class="btn btn-secondary btn-sm" onClick={()=> props.onRemoveTask(task.description)}>remove</button>
                        <button id="btns" type="button" class={filterButton(task)} onClick={()=> props.onToggleTask(task.description)}>{filterTask(task)}</button>
                    </li>
                ).filter((frak) => frak.completeStatus === true))}
            </ol>
        </div>

        )
    } 

    return (<div>
        <ol>
            {props.list.map((task)=>(
                <li key={task.description}>
                    <span>{task.description}</span>
                    <button id="btns" type="button" class="btn btn-secondary btn-sm" onClick={()=> props.onRemoveTask(task.description)}>remove</button>
                    <button id="btns" type="button" class={filterButton(task)} onClick={()=> props.onToggleTask(task.description)}>{filterTask(task)}</button>
                </li>
            ).filter((task) => task.completeStatus === true))}
        </ol>
    </div>)
}

function filterTask(task){
    if (task.completeStatus === false){
        return "incomplete"
    }
    return "complete"
}

function filterButton(task){
    if (task.completeStatus === false){
        return "btn btn-danger btn-sm"
    }
    return "btn btn-success btn-sm"
}
