import React from 'react';

export default function Tasks (props) {

    return (
        <div>
            <ol>
                {props.list.map((task)=>(
                    <li key={task.description}>
                        <span>{task.description}</span>
                        <button id="btns" type="button" class="btn btn-secondary btn-sm" onClick={()=> props.onRemoveTask(task.description)}>remove</button>
                        <button id="btns" type="button" class="btn btn-danger btn-sm" onClick={()=> props.onToggleTask(task.description)}>{filterTaskCompletion(task)}</button>
                    </li>
                ))}
            </ol>
        </div>

    )
}

function filterTaskCompletion(task){
    if (task.completeStatus === false){
        return "incomplete"
    }
    return "complete"
}
